mapper.select:
select * from tbl_member

order by reg_date desc
limit #{queryStartNo}, #{queryPerPageNum}

mapper.update:
UPDATE tbl_member SET user_name = #{user_name},
user_pw = #{user_pw},
email = #{email}, point = #{point}, enabled = #{enabled}, levels = #{levels}, update_date = now() WHERE user_id = #{user_id}
